mapper.select:
select
order_code AS orderCode,
third_code AS thirdCode,
account_code AS accountCode,
inn_id AS innId,
chain_id AS chainId,
trans_amount AS transAmount,
end_of_day AS endOfDay,
remark AS remark,
call_state AS callState,
call_count AS callCount,
create_time AS createTime,
create_oprt AS createOprt,
update_time AS updateTime,
update_oprt AS updateOprt,
third_item_id AS thirdItemId,
ext AS ext
from gs_trans_log
where 1 = 1

	and order_code = #{orderCode}


	and third_code = #{thirdCode}


	and account_code = #{accountCode}


	and inn_id = #{innId}


	and chain_id = #{chainId}


	and trans_amount = #{transAmount}


	and end_of_day = #{endOfDay}


	and remark = #{remark}


	and call_state = #{callState}


	and call_count = #{callCount}


	and create_time = #{createTime}


	and create_oprt = #{createOprt}


	and update_time = #{updateTime}


	and update_oprt = #{updateOprt}


	and third_item_id = #{thirdItemId}


	and ext = #{ext}

select
order_code AS orderCode,
third_code AS thirdCode,
